import "./App.css";
import Header from "./components/header";
import Footer from "./components/footer.js";
import Home from "./pages/home";
import AdminHome from "./pages/adminHome";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  useLocation,
} from "react-router-dom"; // Thêm useLocation ở đây

import Login from "./pages/login";

function App() {
  const location = useLocation();

  // Kiểm tra đường dẫn hiện tại có phải là trang quản trị viên hay không
  const isAdminPage = location.pathname.includes("/admin");
  return (
    <div>
      {!isAdminPage && <Header />}{" "}
      {/* Chỉ hiển thị Header nếu không phải trang admin */}
      <Routes>
        <Route exact path="/" component={Home} />
        <Route exact path="/admin" component={AdminHome} />
      </Routes>
      {!isAdminPage && <Footer />}{" "}
      {/* Chỉ hiển thị Footer nếu không phải trang admin */}
    </div>
  );
}

export default App;
