import React, { useEffect, useRef } from "react";
import $ from "jquery";
import "datatables.net-dt/css/dataTables.dataTables.css";
import "datatables.net";
import "../assets/css/componentsCSS/datatables.css";

const DataTable = ({ columns, data }) => {
  const tableRef = useRef(null); // Tham chiếu đến bảng

  useEffect(() => {
    const table = $(tableRef.current).DataTable({
      data: data.map((item) => columns.map((col) => item[col.key] || "")), // Dữ liệu theo cột
      columns: columns.map((col) => ({ title: col.label })), // Tiêu đề cột
      destroy: true, // Hủy DataTables khi khởi tạo lại
      paging: true, // Bật phân trang
      searching: true, // Bật tìm kiếm
      ordering: true, // Bật sắp xếp
      pageLength: 20, // Số lượng bản ghi mỗi trang
      lengthChange: true,
      responsive: true,
      lengthMenu: [10, 20, 30, 40, 50],
      scrollY: "590px", // Chiều cao tối đa của bảng
      scrollCollapse: true, // Cho phép bảng co lại khi có ít bản ghi hơn chiều cao tối đa
      language: {
        sProcessing: "Đang xử lý...",
        sLengthMenu: "Hiển thị _MENU_ bản ghi",
        sZeroRecords: "Không tìm thấy bản ghi nào phù hợp",
        sInfo: "Đang hiển thị _START_ đến _END_ trong tổng số _TOTAL_ bản ghi",
        sInfoEmpty: "Đang hiển thị 0 đến 0 trong tổng số 0 bản ghi",
        sInfoFiltered: "(được lọc từ _MAX_ bản ghi)",
        sInfoPostFix: "",
        sSearch: "Tìm kiếm:",
        sUrl: "",
        oPaginate: {
          sFirst: "Đầu",
          sPrevious: "Trước",
          sNext: "Tiếp",
          sLast: "Cuối",
        },
      },
    });

    return () => {
      table.destroy(); // Hủy bảng khi component bị unmount
    };
  }, [columns, data]);

  return <table ref={tableRef} className="display" style={{ width: "100%" }} />;
};

export default DataTable;
