import "./App.css";
import Header from "./components/header";
import Footer from "./components/footer.js";
import Home from "./pages/home";
import AdminHome from "./pages/adminHome";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  useLocation,
} from "react-router-dom"; // Thêm useLocation ở đây

import Login from "./pages/login";
import DataTable from "./components/dataTables.jsataTable";
function App() {
  const location = useLocation();

  // Kiểm tra đường dẫn hiện tại có phải là trang quản trị viên hay không
  const isAdminPage = location.pathname.includes("/admin");
  const columns = [
    { key: "id", label: "ID Danh mục" },
    { key: "name", label: "Tên Danh Mục" },
    { key: "createdAt", label: "Ngày Tạo" },
    { key: "createdBy", label: "Người Tạo" },
  ];

  const data = [
    {
      id: 20,
      name: "Thời Trang",
      createdAt: "2024-11-10",
      createdBy: "Đỗ Văn T",
    },
    {
      id: 21,
      name: "Đồ Điện Tử",
      createdAt: "2024-11-11",
      createdBy: "Nguyễn Văn A",
    },
    {
      id: 22,
      name: "Gia Dụng",
      createdAt: "2024-11-12",
      createdBy: "Trần Thị B",
    },
  ];

  return (
    <div>
      {!isAdminPage && <Header />} {/* Hiển thị Header */}
      {!isAdminPage && <Home />} {/* Hiển thị Header */}
      <Routes>
        {/* Định nghĩa route cho trang AdminHome */}
        <Route path="/admin-home" element={<AdminHome key="admin-home" />} />
      </Routes>
      {!isAdminPage && <Footer />}
    </div>
  );
}

export default App;
